// Citation: Lawrence H. Officer, 'Exchange Rates Between the United States Dollar and Forty-one Currencies,' MeasuringWorth, 2021.
// The table(s) present the price of one United States Dollar.

class Country {
  constructor(units, exchange) {
    this.units = units;
    this.exchange = exchange;
    this.start = Math.min(...Object.keys(this.exchange));
    this.end = Math.max(...Object.keys(this.exchange));
  }

  // Get the name of the country's currency in a given year
  currency = (year) => {
    if (this.start <= year) {
      return this.units[
        Math.max(...Object.keys(this.units).filter((x) => x <= year))
      ];
    }
  };

  // Get the exchange rate for a given year
  // If year is not in table, use the nearest prior year (exchange rate was probably fixed, thanks Bretton Woods)
  xr = (year) => {
    if (this.start <= year) {
      return this.exchange[
        Math.max(...Object.keys(this.exchange).filter((x) => x <= year))
      ];
    }
  };}

